import React,{Component} from 'react';
import { Jumbotron } from 'reactstrap';
import {Navbar, NavbarBrand,Nav,NavbarToggler,Collapse,NavItem,Modal,ModalHeader,ModalBody
,Form,FormGroup,Label,Input,Button} from 'reactstrap';
import {NavLink} from 'react-router-dom';

class Header extends Component{

    constructor(props){
        super(props);
        this.state = {
            isNavOpen: false,
            isModalOpen:false
        };
        this.toggleNav =this.toggleNav.bind(this); //in order to use this fn in our jsx code we need to bind this
        this.toggleModal =this.toggleModal.bind(this); 
        this.handleLogin =this.handleLogin.bind(this);
    }

    toggleNav(){
        this.setState({
            isNavOpen: !this.state.isNavOpen
        });
    }

    toggleModal(){
        this.setState({
            isModalOpen: !this.state.isModalOpen
        });
    }

    handleLogin(event){
        this.toggleModal();
        alert('Username:'+ this.username.value +'Password:'+this.password.value + 'Remember:'+this.remember.checked);
        event.preventDefault(); //prevent to reassemble the page
    }

    render(){
      return(  <>
        <Navbar dark expand="md">
        <div className="container">
            <NavbarToggler onClick={this.toggleNav}/>
          <NavbarBrand className="mr-auto" href="/">
              <img src="assets/images/logo.png" height="30" width="41" alt="Ristorante Con Fusion"/>
          </NavbarBrand>
          <Collapse isOpen={this.state.isNavOpen} navbar>
           <Nav navbar>
               <NavItem>
                   <NavLink className="nav-link" to="/home">
                       <span className="fa fa-home fa-lg"></span>Home
                   </NavLink>
               </NavItem>
               <NavItem>
                   <NavLink className="nav-link" to="/home">
                       <span className="fa fa-info fa-lg"></span>About us
                   </NavLink>
               </NavItem>
               <NavItem>
                   <NavLink className="nav-link" to="/home">
                       <span className="fa fa-list fa-lg"></span>Menu
                   </NavLink>
               </NavItem>
               <NavItem>
                   <NavLink className="nav-link" to="/home">
                       <span className="fa fa-address-card fa-lg"></span>Contact us
                   </NavLink>
               </NavItem>
           </Nav>
           <Nav className="ml-auto" navbar>
               <NavItem>
                   <Button outline onClick={this.toggleModal}>
                       <span className="fa fa-sign-in fa-lg"></span>Login
                   </Button>
               </NavItem>
           </Nav>
         </Collapse>
        </div>
        </Navbar>
        <Jumbotron>
            <div className="container">
                <div className="row row-header">
                    <div className="col-12 col-sm-6">
                       <h1>Ristorante Con Fusion</h1>
                       <p>We take inspiration from the World's best cuisines, and create a unique fusion experience. Our lipsmacking creations will tickle your culinary senses!</p>
                    </div>
                </div>

            </div>
        </Jumbotron>
        <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
            <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
            <ModalBody>
                 <Form onSubmit = {this.handleLogin}>
                     <FormGroup>
                         <Label htmlFor="username">Username</Label>
                         <Input type="text" id="username" name="username" innerRef={(input)=> this.username = input}></Input>
                     </FormGroup>
                     <FormGroup>
                         <Label htmlFor="password">Password</Label>
                         <Input type="password" id="password" name="password" innerRef={(input)=> this.password = input}></Input>
                     </FormGroup>
                     <FormGroup check>
                         <Label check>
                             <Input type="checkbox" name="remember" innerRef={(input)=> this.remember = input}/>
                             Remember me
                         </Label>

                     </FormGroup>
                     <Button type="submit" value="submit" color="primary">Login</Button>
                 </Form>
            </ModalBody>
        </Modal>
        </>
      );

    }
}

export default Header;
